import React, { useState } from "react"
import 'font-awesome/css/font-awesome.min.css';


const AddDevice = ({ deviceData = {}, postDevice, updateDevice, showUpdatedDevice }) => {
    const [device, setDevice] = useState({
        id: deviceData.id ?? 0,
        deviceName: deviceData.deviceName ?? "",
        deviceType: deviceData.deviceType ?? "",
        status: deviceData.status ?? "",
    })

    const handleValue = e => {
        setDevice({ ...device, [e.target.name]: e.target.value })
    }

    const submitDevice = e => {
        e.preventDefault()
        debugger;
        if (device.deviceName === "" || device.deviceType === "" || device.status === "") return

        if (deviceData.id) {
            debugger;
            updateDevice(deviceData.id, device)
        } else {
            postDevice(device)
        }
        setDevice({ ...device, "deviceName": "", "deviceType": "", "status": "" })
    }

    const hiderow = e => {
        showUpdatedDevice(deviceData.id);
    }

    const isAdd = !deviceData.id ? true : false;
    return (
        <>
            <td>
                <input
                    type='text'
                    name='deviceName'
                    className="form-control"
                    value={device.deviceName}
                    placeholder='DeviceName'
                    onChange={e => handleValue(e)}
                />
            </td>
            <td>
                <input
                    type='text'
                    name='deviceType'
                    className="form-control"
                    value={device.deviceType}
                    placeholder='DeviceType'
                    onChange={e => handleValue(e)}
                />
            </td>
            <td>
                <input
                    type='text'
                    name='status'
                    className="form-control"
                    value={device.status}
                    placeholder='Status'
                    onChange={e => handleValue(e)}
                />
            </td>
            <td>
                {isAdd ?
                    <input
                        className='btn btn-primary btnsubmit'
                        type='submit'
                        value={`${!deviceData.id ? "Add new Device" : "Save Device"}`}
                        onClick={submitDevice}
                    /> :
                    <i className="fa fa-check fa-2x text-success" onClick={submitDevice}></i>
                }
                {isAdd ? "" : <i className="fa fa-times fa-2x text-muted" onClick={hiderow}></i>}
            </td>
        </>
    )
}

export default AddDevice


