import React, { useState, useEffect } from "react"
import Form from "./AddDevice"
import Tablelist from "./DeviceList"
import 'bootstrap/dist/css/bootstrap.min.css';

import { restHelper } from "../httphelpers/restHelper"

const EditDevice = () => {
    const [devices, setDevices] = useState(null)

    const getUrl = "http://localhost:4000/devices"
    const postUrl = "http://localhost:4000/devices/add-device"
    const putUrl = "http://localhost:4000/devices/update-device"
    const deleteUrl = "http://localhost:4000/devices/delete-device"


    const api = restHelper()

    useEffect(() => {
        getDevices()
    }, [])

    const postDevice = device => {
        api
            .postCreate(`${postUrl}`, { body: device })
            .then(res => getDevices())
            .catch(err => console.log(err))
    }

    const updateDevice = (id, device) => {
        api
            .putUpdate(`${putUrl}/${id}`, { body: device })
            .then(res => getDevices())
            .catch(err => console.log(err))
    }

    const deleteDevice = id => {
        api
            .deletedata(`${deleteUrl}/${id}`, {})
            .then(res => getDevices())
            .catch(err => console.log(err))
    }

    const getDevices = () => {
        api
            .get(`${getUrl}`)
            .then(res => {
                if (res && res.data) {
                    setDevices(res.data)
                }
            })
            .catch(err => console.log(err))
    }
    if (!devices) return null

    return (
        <>
            <h3>Add New Device</h3>
            <table>
                <tbody>
                    <tr>
                        <Form postDevice={postDevice} />
                    </tr>
                </tbody>
            </table>

            <div className='container-fluid'>
                <h3>All Devices</h3>
                <Tablelist
                    devices={devices}
                    setDevices={setDevices}
                    postDevice={postDevice}
                    updateDevice={updateDevice}
                    deleteDevice={deleteDevice}
                />
            </div>
        </>
    )
}

export default EditDevice

