import React from "react"
import Form from "./AddDevice"
import 'bootstrap/dist/css/bootstrap.min.css';
import 'font-awesome/css/font-awesome.min.css';

const Devicetable = ({ devices, postDevice, updateDevice, deleteDevice }) => {
    debugger;

    const showUpdateUser = id => {
        const form = document.getElementsByClassName(`show-form-${id}`)
        form[0].classList.toggle("hide-form")
    }

    const Row = ({ device }) => {
        return (
            <>
                <tr>
                    <td>{device.device_name}</td>
                    <td>{device.device_email}</td>
                    <td>{device.device_location}</td>
                    <td>
                        <i className="fa fa-pencil-square fa-2x text-info" onClick={() => showUpdateUser(device.id)}></i>
                        <i className="fa fa-trash fa-2x text-danger" onClick={() => deleteDevice(device.id)}></i>
                    </td>
                </tr>
                <tr className={`hide-form show-form-${device.id}`}>
                    <Form userData={device} postDevice={postDevice} updateDevice={updateDevice} showUpdateUser={showUpdateUser} />
                </tr>
            </>
        )
    }

    return (

        <table className="table">
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Email</th>
                    <th>Location</th>
                    <th>Actions</th>
                </tr>
            </thead>
            <tbody>
                {devices && devices.map(u => <Row device={u} key={u.id} />)}
            </tbody>
        </table>

    )
}

export default Devicetable




